    private void enterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enterActionPerformed
        // TODO add your handling code here:
        userActionPerformed(evt);
    }//GEN-LAST:event_enterActionPerformed
int getUser=0,setBot=0;
    private void userActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_userActionPerformed
        // TODO add your handling code here:
        String str=user.getText();
        if(!Character.isDigit(str.charAt(0)))
        {
         user.setText("");
            bot.setText("");
            result.setText("                     Invalid Input :( try again");
            return;   
        }
        getUser = Integer.parseInt(str);
        if(getUser/10!=0||getUser>3||getUser<1)
        {
            user.setText("");
            bot.setText("");
            result.setText("                 Invalid Number :( try again");
            return;
        }
        setBot =(int) ((Math.random() * (4 - 1)) + 1);
        bot.setText(String.valueOf(setBot));
        if(getUser==setBot)
        {
            ++score;
            result.setText("                        You Win :) "+"score ="+String.valueOf(score));
        }
        else
        {
           result.setText("                        You Lose :( score="+String.valueOf(score));
        }
    }//GEN-LAST:event_userActionPerformed

    private void endActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_endActionPerformed
       result.setText("     Thanks for playing :)  "+"Final score = "+String.valueOf(score));
       score=0;
    }//GEN-LAST:event_endActionPerformed

    private void resetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_resetActionPerformed
      user.setText("");
       bot.setText("");
       result.setText("");
       score=0;
    }//GEN-LAST:event_resetActionPerformed

